catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   var dateNeeded = (g_form.getValue('Date_needed')); // Get date needed selected by user
   var dateToReturn = (g_form.getValue('Return_date')); // Get the date returned selected by user

   // If the return date is before the date needed, trigger an error message
   if (dateToReturn && dateNeeded && dateToReturn < dateNeeded){
      g_form.addErrorMessage("return date should be after the date needed.");
      g_form.clearValue("Return_date"); // Clear the value 
      return false; // Prevent from submission	
   }
   return true; // allow for submission   
}
